import { useState, useEffect, useMemo, useReducer } from "react";
import "./components/StudentCard";
import StudentCard from "./components/StudentCard";

const initialState = {
  search: "",
  inputValue: "",
  showGrades: false,
  studentData: [],
};

const reducerFunc = (state, action) => {
  switch (action.type) {
    default:
      return { ...state, ...action };
  }
};

let runningTimeout;

function App() {
  const [state, setState] = useReducer(reducerFunc, initialState);
  const [tag, setTag] = useState("");

  const [addTag, setAddTag] = useState("");

  const AddTag = (id) => {
    let newList = state.studentData.map((list) => {
      if (list.id === id) {
        return {
          ...list,
          tags: [...list.tags, addTag],
        };
      }
      return list;
    });
    setState({ studentData: newList });
    setAddTag("");
  };

  const fetchData = async () => {
    let data = await fetch("https://api.hatchways.io/assessment/students").then(
      (res) => res.json().then((data) => data.students)
    );

    let newList = data.map((student) => {
      return { ...student, tags: [] };
    });
    setState({ studentData: newList });
  };

  useEffect(() => {
    fetchData();
  }, []);

  let filteredData = useMemo(() => {
    return state.studentData.filter((data) => {
      if (state.search === "" && tag === "") {
        return data;
      } else if (
        data.firstName.toLowerCase().includes(state.search.toLowerCase()) ||
        data.lastName.toLowerCase().includes(state.search.toLowerCase()) ||
        data.email.toLowerCase().includes(state.search.toLowerCase()) ||
        (data.company.toLowerCase().includes(state.search.toLowerCase()) &&
          data.tags.find((e) =>
            e.toLowerCase().includes(tag.toLowerCase())
          ))
      ) {
        return data;
      }
    });
  }, [state.search, state.studentData, tag]);

  const debouncedOnChange = (e) => {
    const value = e.target.value;
    const id = e.target.id;

    if (runningTimeout) clearTimeout(runningTimeout);
    runningTimeout = setTimeout(() => {
      setState({ [id]: value });
    }, 500);
  };

  return (
    <div className="clearfix">
      <div className="wrapper">
        <img
          className="search-icon"
          src="data:image/svg+xml;utf8;base64,"
        />
        <input
          id="search" //necessary for setState action
          type="text"
          onChange={debouncedOnChange}
          placeholder="Search by name, company or email"
          className="search"
        />
        <img
          className="search-icon2"
          src="data:image/svg+xml;utf8;base64,"
        />

        <input
          // id="tag"
          value={tag}
          type="text"
          onChange={(e) => setTag(e.target.value)}
          placeholder="Search by tag"
          className="search"
        />
      </div>

      <div className="row">
        {filteredData.map((data, index) => {
          return (
            <StudentCard
              data={data}
              key={index}
              addTag={addTag}
              setAddTag={setAddTag}
              AddTag={AddTag}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
